mapper.delete:
delete from 
	board
where
	board_no = #{board_no}
and
	member_id = #{member_id}

mapper.update:
update
	board
set
	board_title = #{board_title}
	,board_content = #{board_content}

			,board_savedfile = #{board_savedfile}
			,board_originfile = #{board_originfile}

where
	board_no = #{board_no}
and
	member_id = #{member_id}

